INSERT INTO public."Entity" (id, title, description, "createdAt", type, "updatedAt", "deletedAt", "imgUrl", published) VALUES (98, 'Бюро добрых дел', 'Благотворительный фонд «БЮРО ДОБРЫХ ДЕЛ» оказывает материальную и социальную помощь детям, оставшимся без попечения родителей. Фонд занимается благоустройством детских домов и приютов, а также закупает товары, необходимые для жизни и творчества их подопечных. Помимо этого мы помогаем детям с выбором будущей профессии, открываем «социальные гостиницы», где дети осваивают все необходимые для будущей взрослой жизни навыки, устраиваем культурно-просветительские и спортивные мероприятия. <br/> <a href="http://burodd.ru">www.burodd.ru</a> ', '2016-09-23 13:50:13.934000 +03:00', 'fund', '2016-10-21 14:23:34.493000 +03:00', null, 'entities/entity-1477048900308.png', true);
