INSERT INTO public."Entity" (id, title, description, "createdAt", type, "updatedAt", "deletedAt", "imgUrl", published) VALUES (45, 'Верю в чудо', 'Благотворительный центр "Верю в чудо" - сообщество неравнодушных людей, помогающих детям Калининградской области, находящимся в трудной жизненной ситуации. С 2007 года организация реализует комплекс долгосрочных благотворительных программ, направленных на поддержку детей с тяжелыми заболеваниями и их родителей, малышей "без мам" в детских больницах, детей-сирот и детей, оставшихся без попечения родителей. <br/> <a href="http://deti39.com">www.deti39.com</a> ', '2016-09-23 13:09:15.162000 +03:00', 'fund', '2016-10-18 18:07:21.986000 +03:00', null, 'entities/entity-1475828776786.png', true);
